<template>
  <div class="section">
        <h1 class="title is-3">Company Information</h1>
    <b-field label="Company Name">
      <b-input v-model="information.name"></b-input>
    </b-field>
    <b-field label="Contact Email">
        <b-input v-model="information.contact"></b-input>
    </b-field>
    <b-field label="LinkedIn for Your Company">
      <b-input v-model="information.linkedIn"></b-input>
    </b-field>
    <b-field label="Description">
      <b-input v-model="information.description"></b-input>
    </b-field>
    <b-field label="Company Website">
        <b-input v-model="information.website"></b-input>
    </b-field>
    
    <b-field label="Company Logo">
        
    </b-field>
    <input @change="onChangeImage" class="button" type="file" accept="image/*"></input>
     <br>  <img v-bind:src="information.image" class="user"></img>
    <br>

    <b-button @click="save()">Save</b-button>
  </div>
</template>

<script>
import firebase from 'firebase'

export default {
  name: 'Info',
  components: {
  },
  data(){
    return {      
      information: {
        name: null,
        contact: null,
        linkedIn: null,
        description: null,
        website: null,
        image: `data:image/png;base64,`
      }
    }
  },
  created(){
    var firebaseConfig = {
    apiKey: "AIzaSyAWnWmesrGAEnm8khsANTCLiivFHhC4LtI",
    authDomain: "lean-job-finder.firebaseapp.com",
    databaseURL: "https://lean-job-finder.firebaseio.com",
    projectId: "lean-job-finder",
    storageBucket: "lean-job-finder.appspot.com",
    messagingSenderId: "481800294263",
    appId: "1:481800294263:web:4aa800218f2bd905729b72"
  };
    if(firebase.apps.length === 0){
          firebase.initializeApp(firebaseConfig);
    }
    if(!firebase.auth().currentUser){
      this.$router.push("/login");
    }

    firebase.firestore().collection("companies").doc(firebase.auth().currentUser.uid).get().then(doc => {
      this.information = doc.data();
      this.image = doc.data().image;
    })
  },
  methods:{
    save(){
        firebase.firestore().collection("companies").doc(firebase.auth().currentUser.uid).update(this.information).then(() => {
          this.$router.push("/")
        });
    },

    onChangeImage(e){
      const selected = e.target.files[0];
      const reader = new FileReader();

      reader.onload = (e) => {
        this.information.image = e.target.result;
        console.log(this.information.image)
      };
      reader.readAsDataURL(selected)
    }
  }
}
</script>
<style scoped>
.user {
  width: 128px;
  height: 128px;
  margin:12px;
}
</style>